(79, 'widget_text', 'a:6:{i:1;a:0:{}i:2;a:3:{s:5:"title";s:18:"Vị trí dự án";s:4:"text";s:117:"<iframe src="https://www.google.com/maps/d/embed?mid=18TO3dX7-a9FL17YlZ7dqJBc5z4M" width="640" height="480"></iframe>";s:6:"filter";s:7:"content";}i:4;a:3:{s:5:"title";s:38:"CAM KẾT TỪ ĐƠN VỊ PHÂN PHỐI";s:4:"text";s:848:"<ul>\r\n	<li><span style="color: #444444;">Đội ngũ chuyên viên tư vấn tốt nghiệp đại học, trung thực, nhiệt tình</span></li>	\r\n	<li><span style="color: #444444;">Mọi thủ tục pháp lý khách hàng đều được thực hiện trực tiếp với CĐT Vinhomes</span></li>\r\n<li><span style="color: #444444;">Cung cấp thông tin nhanh chóng, chính xác và cập nhật nhất từ chủ đầu tư</span></li>\r\n	<li><span style="color: #444444;">Hỗ trợ tư vấn trực tiếp, chuyên sâu giúp quý khách tìm căn hộ phù hợp với gia đình</span></li>\r\n	<li><span style="color: #444444;">Trực tiếp hướng dẫn quý khách thăm quan căn hộ mẫu</span></li>\r\n	<li><span style="color: #444444;">Hỗ trợ mọi thủ tục giấy tờ liên quan trong quá trình mua bán</span></li>\r\n</ul>";s:6:"filter";s:7:"content";}i:5;a:3:{s:5:"title";s:28:"LIÊN HỆ PHÒNG KINH DOANH";s:4:"text";s:680:"<ul>\r\n<li><span style="color: #444444;"><strong>Đại lý chiến lược Newstarland</strong></span></li>\r\n<li><span style="color: #444444;">Email:<strong> batdongsanvinhomes.cskh@gmail.com</strong></span></li>\r\n<li><span style="color: #444444;">Địa chỉ:  	TTTM Vincom Long Biên, Khu đô thị sinh thái Vinhomes Riverside, Quận Long Biên, Hà Nội</span></li>\r\n		<li><span style="color: #444444;">Hotline:<strong> 091 597 6556</strong></span></li>\r\n<li><span style="color: #444444;">Email:<strong> batdongsanvinhomes.cskh@gmail.com</strong></span></li>\r\n         <li><span style="color: #444444;">Website: <strong>vinhometheharmony.com</strong></span></li>\r\n\r\n</ul>";s:6:"filter";s:7:"content";}i:6;a:3:{s:5:"title";s:20:"ĐĂNG KÝ TƯ VẤN";s:4:"text";s:9901:"<form action="https://docs.google.com/forms/u/0/d/e/1FAIpQLSdJvAXc8jRM3Gzs7-Ie-1roLfZUSgNVVKB